INSERT INTO core.ticket (document) VALUES ('{"status": "Resolved (Support Pipeline)", "ticket_with_messages": {"content": "How to make a query like \"SELECT field AS f1 ...\" in flutter -> \"AS\" does not work.", "subject": "Flutter", "messages": [{"text": "Email: nextlinguamanagement@gmail.com\nTicket name: Flutter\nTicket description: How to make a query like \"SELECT field AS f1 ...\" in flutter -> \"AS\" does not work.\nType: Problem\nSeverity: Low\nProject reference: dzjfafuldkfwnxnknsop\n", "type": "MESSAGE", "subject": "Flutter", "direction": "INCOMING", "rich_text": "<div>Email: nextlinguamanagement@gmail.com</div><div>Ticket name: Flutter</div><div>Ticket description: How to make a query like &quot;SELECT field AS f1 ...&quot; in flutter -&gt; &quot;AS&quot; does not work.</div><div>Type: Problem</div><div>Severity: Low</div><div>Project reference: dzjfafuldkfwnxnknsop</div>", "created_at": "2022-09-27T10:27:52.442Z", "message_id": "ccf1495a-a91e-468c-9d0a-35d2aecbb2e0"}, {"text": "Hi,\n\nThank you for contacting Supabase support.\nIt’s done exactly the same as how you would do it in js SDK. The column name followed by :  followed by the alias name!\n\nYou can check this guide here : https://supabase.com/docs/reference/javascript/select\n\nRegards,\nMonica Kh.\nSupabase Support", "type": "MESSAGE", "subject": "Flutter", "direction": "OUTGOING", "rich_text": "<div style=\"text-align: left\">Hi,</div><br><div style=\"text-align: left\">Thank you for contacting Supabase support.</div><div style=\"text-align: left\">It’s done exactly the same as how you would do it in js SDK. The column name followed by <code>:</code>  followed by the alias name!</div><br><div style=\"text-align: left\">You can check this guide here : <a href=\"https://supabase.com/docs/reference/javascript/select\" target=\"_blank\" rel=\"noopener noreferrer\">https://supabase.com/docs/reference/javascript/select</a></div><br><div style=\"text-align: left\">Regards,</div><div style=\"text-align: left\">Monica Kh.</div><div style=\"text-align: left\">Supabase Support</div>", "created_at": "2022-09-27T11:20:19.876Z", "message_id": "41dab40d512942d49c5c08bb47c355b2"}, {"text": "Thank you very much!\nIt worked. But it is \"alias:column name\"\n\nEl 27/09/2022 a las 13:20, Monica ElKhoury from supabase.io escribió:\n", "type": "MESSAGE", "subject": "Re: Flutter", "direction": "INCOMING", "rich_text": "<p>Thank you very much!<br> It worked. But it is \"alias:column name\"<br> </p> \n<div class=\"moz-cite-prefix\">\n El 27/09/2022 a las 13:20, Monica ElKhoury from supabase.io escribió: \n<br> \n</div>", "created_at": "2022-09-27T11:45:15Z", "message_id": "c37d60f1-a1fe-43a2-93f6-6554f1a0673e"}, {"text": "Hi,\n\nGlad to hear your problem is gone!\nIm gonna mark your support ticket as resolved, but feel free to create a new one if you encounter any other issue, we''d be happy to help.\n\nRegards,\nMonica Kh.\nSupabase Support", "type": "MESSAGE", "subject": "Re: Flutter", "direction": "OUTGOING", "rich_text": "<div style=\"text-align: left\">Hi,</div><br><div style=\"text-align: left\">Glad to hear your problem is gone!</div><div style=\"text-align: left\">Im gonna mark your support ticket as resolved, but feel free to create a new one if you encounter any other issue, we&#x27;d be happy to help.</div><br><div style=\"text-align: left\">Regards,</div><div style=\"text-align: left\">Monica Kh.</div><div style=\"text-align: left\">Supabase Support</div>", "created_at": "2022-09-27T11:46:31.159Z", "message_id": "b889de6d47c541e7b5ac5e9da6f2afbf"}, {"text": "Thank you Monica!\n\nEl 27/09/2022 a las 13:46, Supabase escribió:\n", "type": "MESSAGE", "subject": "Re: Flutter", "direction": "INCOMING", "rich_text": "<p>Thank you Monica!</p> \n<div class=\"moz-cite-prefix\">\n El 27/09/2022 a las 13:46, Supabase escribió: \n<br> \n</div>", "created_at": "2022-09-27T12:24:51Z", "message_id": "2cf407cb-d78a-45c5-8317-b20dfe183261"}], "ticket_id": "1127028120"}}');
