INSERT INTO core.ticket (document) VALUES ('{"status": "Resolved (Support Pipeline)", "ticket_with_messages": {"content": "Hi,\n\nWe keep running into an issue where the storage limit will reset when we\nchange the database size add-on, or rather we think the database add-on\nchange may be the cause. We''ve noticed that it resets after we have made a\nchange to the database add-on but haven''t tried to reproduce it as we''d\nhave to spend money in order to do so.\n\nSorry for not reporting this one as a github issue but we didn''t want to\nhave to add a database add-on to try and reproduce.\n\nRegards,\nNicola", "subject": "Storage limit resets to 50 on add-on upgrade", "messages": [{"text": "Hi,\n\nWe keep running into an issue where the storage limit will reset when we\nchange the database size add-on, or rather we think the database add-on\nchange may be the cause. We''ve noticed that it resets after we have made a\nchange to the database add-on but haven''t tried to reproduce it as we''d\nhave to spend money in order to do so.\n\nSorry for not reporting this one as a github issue but we didn''t want to\nhave to add a database add-on to try and reproduce.\n\nRegards,\nNicola", "type": "MESSAGE", "subject": "Storage limit resets to 50 on add-on upgrade", "direction": "INCOMING", "rich_text": "<div dir=\"ltr\"> \n<div class=\"gmail_default\" style=\"font-family:verdana,sans-serif\">\n Hi, \n<br> \n</div> \n<div class=\"gmail_default\" style=\"font-family:verdana,sans-serif\"> \n<br> \n</div> \n<div class=\"gmail_default\" style=\"font-family:verdana,sans-serif\">\n We keep running into an issue where the storage limit will reset when we change the database size add-on, or rather we think the database add-on change may be the cause. We''ve noticed that it resets after we have made a change to the database add-on but haven''t tried to reproduce it as we''d have to spend money in order to do so. \n<br> \n</div> \n<div class=\"gmail_default\" style=\"font-family:verdana,sans-serif\"> \n<br> \n</div> \n<div class=\"gmail_default\" style=\"font-family:verdana,sans-serif\">\n Sorry for not reporting this one as a github issue but we didn''t want to have to add a database add-on to try and reproduce. \n<br> \n<br> \n</div> \n<div class=\"gmail_default\" style=\"font-family:verdana,sans-serif\">\n Regards, \n<br> \n</div> \n<div class=\"gmail_default\" style=\"font-family:verdana,sans-serif\">\n Nicola \n<br> \n</div> \n</div>", "created_at": "2022-09-27T11:19:06Z", "message_id": "7e099f28-0ade-4afd-9551-ad49985eeac2"}, {"text": "Hi,\n\nThanks for reporting!\nI have forwarded this to the rest of the team, so they can further investigate it.\n\nRegards,\nMonica Kh.\nSupabase Support", "type": "MESSAGE", "subject": "Re: Storage limit resets to 50 on add-on upgrade", "direction": "OUTGOING", "rich_text": "<div style=\"text-align: left\">Hi,</div><br><div style=\"text-align: left\">Thanks for reporting!</div><div style=\"text-align: left\">I have forwarded this to the rest of the team, so they can further investigate it.</div><br><div style=\"text-align: left\">Regards,</div><div style=\"text-align: left\">Monica Kh.</div><div style=\"text-align: left\">Supabase Support</div>", "created_at": "2022-09-27T11:57:45.981Z", "message_id": "076832e37f6c48e8980f68a7ff7e61c5"}, {"text": "Hi Nicola, \n\nI can confirm this is a bug and we working on a fix now. Thanks for reporting!\n\nRegards,\nInian", "type": "MESSAGE", "subject": "Re: Storage limit resets to 50 on add-on upgrade", "direction": "OUTGOING", "rich_text": "<div style=\"text-align: left\">Hi Nicola, </div><br><div style=\"text-align: left\">I can confirm this is a bug and we working on a fix now. Thanks for reporting!</div><br><div style=\"text-align: left\">Regards,</div><div style=\"text-align: left\">Inian</div>", "created_at": "2022-10-07T04:22:31.305Z", "message_id": "7bab5fbdaa834836a21c89e2bf73e7b4"}, {"text": "Hi Nicola, the fix has been deployed. \n\nThanks once again for reporting the bug.", "type": "MESSAGE", "subject": "Re: Storage limit resets to 50 on add-on upgrade", "direction": "OUTGOING", "rich_text": "<div style=\"text-align: left\">Hi Nicola, the fix has been deployed. </div><br><div style=\"text-align: left\">Thanks once again for reporting the bug. </div>", "created_at": "2022-10-12T13:41:21.762Z", "message_id": "a4db2c93d1674436b1135bea4c165ade"}, {"text": "Hi,\n\nSorry, I was busy at the time and forgot to say thanks! I really appreciate\nthe time taken to reproduce and fix.\n\nCheers,\nNicola\n\n", "type": "MESSAGE", "subject": "Re: Storage limit resets to 50 on add-on upgrade", "direction": "INCOMING", "rich_text": "<div dir=\"ltr\"> \n<div class=\"gmail_default\" style=\"font-family:verdana,sans-serif\">\n Hi, \n<br> \n<br> \n</div> \n<div class=\"gmail_default\" style=\"font-family:verdana,sans-serif\">\n Sorry, I was busy at the time and forgot to say thanks! I really appreciate the time taken to reproduce and fix. \n<br> \n<br> \n</div> \n<div class=\"gmail_default\" style=\"font-family:verdana,sans-serif\">\n Cheers, \n<br> \n</div> \n<div class=\"gmail_default\" style=\"font-family:verdana,sans-serif\">\n Nicola \n<br> \n</div> \n</div> \n<br>", "created_at": "2022-12-29T23:30:10Z", "message_id": "22a82b5f-4c7d-4176-857f-289f6808c7c3"}, {"text": "Hi,\n\nAs your problem is gone, I am gonna mark this support request as resolved - but if you ever need our assistance while using Supabase, you already know where to find us.\n\nHave a wonderful day!\n\nBest regards,\nJames Wong​\nSupabase Support Engineer", "type": "MESSAGE", "subject": "Re: Storage limit resets to 50 on add-on upgrade", "direction": "OUTGOING", "rich_text": "<div style=\"text-align: left\">Hi,</div><br><div style=\"text-align: left\">As your problem is gone, I am gonna mark this support request as resolved - but if you ever need our assistance while using Supabase, you already know where to find us.</div><br><div style=\"text-align: left\">Have a wonder